from typing import List
import copy
from collections import Counter

class Solution:
    def split_list(self, s: str, n: int, maxVal: int, initInd: int= 0) -> object:
        '''
        將list分割 根據 n (個)切割
        '''
        maxVal = maxVal if(maxVal <= len(s)) else len(s)
        for idx in range(initInd, maxVal, n):
            yield s[idx: idx + n]
    def split_list2list(self, s: str, n: int, maxVal: int, initInd: int= 0) -> list:
        return list(self.split_list(s, n, maxVal, initInd))
    def findSubstring(self, s: str, words: List[str]) -> List[int]:
        strCount = len(s)
        allWordNum = {}#單字要出現幾次
        wordLength = len(words[0])#題目說每個 words 長度相同
        for w in words:#統計單字
            if(not (w in allWordNum)):
                allWordNum[w] = 1 
            else:
                allWordNum[w] += 1 
        
        rlt = []
        allWordNumSum = sum(allWordNum.values())#計算
        for i in range(strCount):
            copyAllWordNum = copy.copy(allWordNum)#深拷貝
            splitList = self.split_list2list(s, wordLength, i+ allWordNumSum * wordLength, i)
            splitCounter = Counter(splitList)#轉成 Counter 將 array 裡面出現的數面統計直接剪掉
            for cawn in copyAllWordNum:
                if(cawn in splitCounter):
                    temp = splitCounter[cawn]
                    splitCounter[cawn] -= copyAllWordNum[cawn]
                    copyAllWordNum[cawn] -= temp
                else:
                    break
                splitCounterValues = splitCounter.values()
                copyAllWordNumValues = copyAllWordNum.values()
                if(max(splitCounterValues) == min(splitCounterValues) == 0 and
                max(copyAllWordNumValues) == min(copyAllWordNumValues) == 0):#如果都符合直接回傳
                    rlt.append(i)
                    break
        return rlt
            
                
                
